muclient.script:
local simplewindow = require('simplewindow')
local samplewindow = nil


local function CreateConfig()
    return 
        simplewindow.CreateConfig {
        Id = 'samplesimple',
        Title = 'SAMPLE WINDOW',
        UpdateInterval = 1
    }
end

local function CreateTheme()
 return
        simplewindow.CreateTheme {
        BorderColor = '#D1FAFF',
        BorderWidth = 3,
        TitleClasses = {'title'},
        BodyClasses = 'm-lg body', -- add a large margin to our body
        Classes = {
            simplewindow.CreateClass {
                Name = 'title',
                FontSize = 12,
                FontColor = {0,0,0}, -- can use rbg table for colors also
                BackColor = '#6A8EAE',
                Padding = 1,
                Margin = 0
            },
            simplewindow.CreateClass {
                Name = 'body',
                Margin = 5
            },
            simplewindow.CreateClass {
                Name = 'header',
                FontSize = 10,
                Underline = true,
                Alignment = simplewindow.Alignments.Center
            },
            simplewindow.CreateClass {
                Name = 'footer',
                BackColor = 'orange',
                FontColor = 'white',
                Margin = {Left = 0, Top = 15, Right = 0, Bottom = 0},
                Alignment = simplewindow.Alignments.Center
            },
            -- you can have classes as simple as custom color names
            simplewindow.CreateClass {
                Name = 'peachy',
                FontColor = {255, 218, 185}
            },
            simplewindow.CreateClass {
                Name = 'property',
                FontColor = 'white',
                Bold = true,
                Alignment = {X = simplewindow.Alignments.Start, Y = simplewindow.Alignments.Center}
            },
            simplewindow.CreateClass {
                Name = 'stat',
                FontColor = {66, 134, 244},
                Alignment = {X = simplewindow.Alignments.End, Y = simplewindow.Alignments.Center}
            }
        }
    }
end

local function DefineMainView(v)
  local statContainer =
            v.AddContainer {
            Sizes = {{Name = 'xs', Percent = 0}, {Name = 'md', Percent = 40}}, -- hide content it when smaller than medium
            ContentSizes = {{Name = 'xs', Percent = 50}},
            ContentClasses = 'stat'
        }

        statContainer.AddContent {
            Text = 'Stats',
            Classes = 'header peachy',
            Sizes = {{Name = 'xs', Percent = 100}}
        }

        statContainer.AddContent {
            Text = 'Level',
            Classes = 'property'
        }
        statContainer.AddContent {
            Text = '1'
        }

        statContainer.AddContent {
            Text = 'Str',
            Classes = 'property'
        }
        statContainer.AddContent {
            Text = '150'
        }

        statContainer.AddContent {
            Text = 'Health',
            Classes = 'property'
        }
        statContainer.AddContent {
            Text = '15000'
        }

        statContainer.AddContent {
            Text = 'Mana',
            Classes = 'property'
        }
        statContainer.AddContent {
            Text = '15000'
        }

        local textContainer =
            v.AddContainer {
            Sizes = {{Name = 'xs', Percent = 100}, {Name = 'md', Percent = 60}},
            ContentSizes = {{Name = 'xs', Percent = 100}, {Name = 'lg', Percent = 50}},
            ContentClasses = 'm-lg warning-b'
        }

        textContainer.AddContent {
            Classes = 'header peachy',
            Sizes = {{Name = 'xs', Percent = 100}}
        }

        -- janky fade
        local fader = simplewindow.SantizeColor('#157145')

        textContainer.AddContent {Classes = 'm-lg underline', BackColor = fader}
        textContainer.AddContent {BackColor = simplewindow.ColorAdjust(fader, -.2)}
        textContainer.AddContent {BackColor = simplewindow.ColorAdjust(fader, -.4)}
        textContainer.AddContent {BackColor = simplewindow.ColorAdjust(fader, -.6)}

        -- for our footer we will show a different footer depending on the window side.
        local footerContainer =
            v.AddContainer {
            Sizes = {{Name = 'xs', Percent = 100}},
            ContentClasses = {'footer'}
        }

        footerContainer.AddContent {
            Sizes = {{Name = 'xs', Percent = 0}, {Name = 'md', Percent = 100}},
            Text = 'The larger end.'
        }

        footerContainer.AddContent {
            Text = 'The smaller end.',
            Sizes = {{Name = 'xs', Percent = 100}, {Name = 'md', Percent = 0}},
            BackColor = 'purple'
        }
end

function OnPluginInstall()
    samplewindow = simplewindow.CreateWindow(CreateConfig(),CreateTheme())

    sampleview = 
        samplewindow.CreateView {
        Name = 'Main'
    }
    sampleview.OnUpdate = DefineMainView
    samplewindow.RegisterView(sampleview)

end

function OnPluginTick()
    if (samplewindow) then
        samplewindow.Tick()
    end
end
